source(file.path(.libPaths(),"tools","R","tools"))# fix to .convertFilePathToAbsolute otherwise a ":" is postpended # to the dir name .convertFilePathToAbsolute <- function(dir) dirinst.packs <- function(){pkgs <-c("base","ctest","eda","lqs","methods","modreg","mva","nls","splines","stepfun","tools","ts")cat("doing package:")for( pp in pkgs){ cat(" ",pp) indir <- file.path(R.home(),"src","library",pp) outdir <- file.path(.libPaths(),pp) .installPackageIndices(indir, outdir) ns <- file.path(indir,"NAMESPACE") if(file.exists(ns))  file.copy(ns,file.path(outdir,"NAMESPACE")) .installPackageRdIndices(indir, outdir)} cat("\npackage installation completed\n")}inst.packs()